import React, { useState, useEffect } from 'react';
import './make.css';

function Make({ onPriceChange }) {
    const [selectedOptions, setSelectedOptions] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);

    const prices = {
        'productl.jpg': 2379,
        'productn.jpg': 1629,
        'productle.jpg': 1499,
        'productln.jpg': 4008,
        'productlle.jpg': 3878,
        'productnle.jpg': 3128,
        'productlnl.jpg': 5507,
    };

    const combinationPrices = {
        'productl.jpg+productn.jpg': 4008,
        'productl.jpg+productle.jpg': 3878,
        'productle.jpg+productn.jpg': 3128,
        'productl.jpg+productle.jpg+productn.jpg': 5507,
    };

    const calculateTotalPrice = (selectedMods) => {
        const sortedMods = selectedMods.sort().join('+');
        return combinationPrices[sortedMods] || selectedMods.reduce((total, mod) => total + prices[mod], 0);
    };

    useEffect(() => {
        const newTotalPrice = calculateTotalPrice(selectedOptions);
        setTotalPrice(newTotalPrice);
        onPriceChange(newTotalPrice);

        // Debugging logs
        console.log('Selected modules:', selectedOptions);
        console.log('Total price:', newTotalPrice);
    }, [selectedOptions, onPriceChange]);

    const handleOptionClick = (image) => {
        setSelectedOptions((prevSelectedOptions) => {
            if (prevSelectedOptions.includes(image)) {
                // If the option is already selected, remove it and update the price
                const updatedOptions = prevSelectedOptions.filter(option => option !== image);
                const newTotalPrice = calculateTotalPrice(updatedOptions);
                setTotalPrice(newTotalPrice);
                onPriceChange(newTotalPrice);
                return updatedOptions;
            } else {
                // If the option is not selected, add it to the selection and update the price
                const updatedOptions = [...prevSelectedOptions, image];
                const newTotalPrice = calculateTotalPrice(updatedOptions);
                setTotalPrice(newTotalPrice);
                onPriceChange(newTotalPrice);
                return updatedOptions;
            }
        });
    };

    const isActive = (image) => selectedOptions.includes(image);

    return (
        <div className="yarbo-section">
            <div className="buttons-container">
                <div className="moduls">
                    <h2>Modulit</h2>
                    <button onClick={() => handleOptionClick('productl.jpg')} className={isActive('productl.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Lumilinko {prices['productl.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Lumilingosta</a>
                    <button onClick={() => handleOptionClick('productn.jpg')} className={isActive('productn.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Nurmikonleikkuri {prices['productn.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Nurmikonleikkurista</a>
                    <button onClick={() => handleOptionClick('productle.jpg')} className={isActive('productle.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Puhallin {prices['productle.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Puhaltimesta</a>
                </div>
            </div>
            <div className="image-display">
                {selectedOptions.length > 0 && (
                    <img src={`./images/make/${selectedOptions.join('+')}`} alt="Selected Combination" />
                )}
            </div>
        </div>
    );
}

export default Make;
