import React, { useState } from 'react';
import './make.css';

function Make() {
    const [selectedOptions, setSelectedOptions] = useState([]); // Use state to keep track of selected options

    const handleOptionClick = (image) => {
        setSelectedOptions(prevSelectedOptions => {
            if (prevSelectedOptions.includes(image)) {
                return prevSelectedOptions.filter(option => option !== image);
            } else {
                return [...prevSelectedOptions, image];
            }
        });
    };

    const getCombinedImage = () => {
        const combinationMap = {
            'image1.jpg+image2.jpg': 'combined1and2.jpg',
            'image1.jpg+image3.jpg': 'combined1and3.jpg',
            // Lisää lisää yhdistelmät tähän, esim.:
            'image1.jpg+image4.jpg': 'combined1and4.jpg',
            'image2.jpg+image3.jpg': 'combined2and3.jpg',
        };

        const key = selectedOptions.sort().join('+');
        return combinationMap[key] || selectedOptions;
    };

    const combinedImage = getCombinedImage();

    return (
        <div className="yarbo-section">
            <div className="options">
                <button onClick={() => handleOptionClick('productl.webp')} className={selectedOptions.includes('productl.webp') ? 'active' : ''}>lumilinko</button>
                <button onClick={() => handleOptionClick('image2.jpg')} className={selectedOptions.includes('image2.jpg') ? 'active' : ''}>nurmikonleikkuri</button>
                <button onClick={() => handleOptionClick('image3.jpg')} className={selectedOptions.includes('image3.jpg') ? 'active' : ''}>Option 3</button>
                <button onClick={() => handleOptionClick('image4.jpg')} className={selectedOptions.includes('image4.jpg') ? 'active' : ''}>Option 4</button>
                <button onClick={() => handleOptionClick('image5.jpg')} className={selectedOptions.includes('image5.jpg') ? 'active' : ''}>Option 5</button>
            </div>
            <div className="image-display">
                {Array.isArray(combinedImage) ? combinedImage.map((option, index) => (
                    <img key={index} src={`imagines/make/${option}`} alt={`Selected Option ${index + 1}`} />
                )) : (
                    <img src={`imagines/make/${combinedImage}`} alt="Combined Option" />
                )}
            </div>
        </div>
    );
}

export default Make;
