import React, { useState } from 'react';
import './make.css';
import Popup from './popup';

function Lisavarusteet() {
    const [selected, setSelected] = useState(false);
    const [popupImage, setPopupImage] = useState(null);

    const handleButtonClick = () => {
        setSelected(!selected);
    };

    const openPopup = (image) => {
        setPopupImage(image);
    };

    const closePopup = () => {
        setPopupImage(null);
    };

    return (
        <div className="yarbo-section lisavarusteet">
            <div className="buttons-container">
                <div className="lisavarusteet-options">
                    <h2>Lisävarusteet</h2>
                    <button
                        onClick={handleButtonClick}
                        className={selected ? 'active' : ''}
                    >
                        <span className="checkmark"></span>Lisävaruste 1
                    </button>
                    <a href="#" className="link-button" onClick={() => openPopup('./images/lisavarusteet/lisavaruste1.png')}>Lisätietoa Lisävarusteesta 1</a>
                </div>
            </div>
            <div className="image-display">
                {selected && (
                    <img src="./images/make/lisavaruste1.png" alt="Lisävaruste 1 Option" />
                )}
            </div>
            {popupImage && <Popup image={popupImage} onClose={closePopup} />}
        </div>
    );
}

export default Lisavarusteet;
