import React, { useState, useRef, useEffect } from 'react';


const texts = [
    { text: "First text overlay", time: 5, color: "#FFFFFF", bgColor: "#000000" },
    { text: "Second text overlay", time: 10, color: "#FFFFFF", bgColor: "#000000" },
    // Add more text overlays here
];

const VideoPlayer = () => {
    const [currentTextIndex, setCurrentTextIndex] = useState(0);

    useEffect(() => {
        const textTimer = setInterval(() => {
            setCurrentTextIndex((prevIndex) => (prevIndex + 1) % texts.length);
        }, texts[currentTextIndex].time * 1000);

        return () => clearInterval(textTimer);
    }, [currentTextIndex]);

    return (
        <div className="video-container">
            <img
                src="" // Replace with the actual path to your image
                alt="Etukuva"
                className="etukuva"
            />
            <div className="video-text" style={{
                color: texts[currentTextIndex].color,
                backgroundColor: texts[currentTextIndex].bgColor,
            }}>
                {texts[currentTextIndex].text.split('\n').map((line, index) => (
                    <React.Fragment key={index}>
                        {line === "" ? (
                            <p style={{ margin: 0 }}>&nbsp;</p> // Render an empty paragraph with a space
                        ) : (
                            <p style={{ margin: 0 }}>{line}</p>
                        )}
                    </React.Fragment>
                ))}
            </div>
        </div>
    );
};

export default VideoPlayer;
