import React, { useState } from 'react';
import Menu2 from "./menu2";
import Make from "./make";
import Lisaosat from "./lisaosat";
import Prices from "./prices";
import Core from "./core";

const BuildPage = () => {
    const [totalPrice, setTotalPrice] = useState(0);

    const handlePriceChange = (deltaPrice) => {
        setTotalPrice(prevTotal => prevTotal + deltaPrice);
    };

    return (
        <div>
            <Menu2/>
            <div id="Core">
                <Core onPriceChange={handlePriceChange} />
            </div>
            <div id="Make">
                <Make onPriceChange={handlePriceChange} />
            </div>
            <div id="Lisaosat">
                <Lisaosat onPriceChange={handlePriceChange} />
            </div>
            <div id="Prices">
                <Prices />
            </div>
            <div className="total-price">
                <h3>Kokonaisuus Hinta: {totalPrice.toLocaleString()}â‚¬</h3>
            </div>
        </div>
    );
}

export default BuildPage;
