import React, { useState } from 'react';
import './make.css';

function Make({ onPriceChange }) {
    const [selectedOptions, setSelectedOptions] = useState([]);
    const prices = {
        'productl.jpg': 2379, // Lumilinko
        'productn.jpg': 1629, // Nurmikonleikkuri
        'productle.jpg': 1499 // Puhallin
    };

    const isModul = (image) => ['productl.jpg', 'productn.jpg', 'productle.jpg'].includes(image);

    const handleOptionClick = (image) => {
        setSelectedOptions(prevSelectedOptions => {
            let updatedOptions;
            if (prevSelectedOptions.includes(image)) {
                updatedOptions = prevSelectedOptions.filter(option => option !== image);
                onPriceChange(-prices[image]);
            } else {
                updatedOptions = [...prevSelectedOptions, image];
                onPriceChange(prices[image]);
            }
            return updatedOptions;
        });
    };

    const getModulsCombiOrSingle = () => {
        const combinationMap = {
            'productl.jpg+productn.jpg': 'productln.jpg',
            'productl.jpg+productle.jpg': 'productlle.jpg',
            'productle.jpg+productn.jpg': 'productnle.jpg',
            'productl.jpg+productle.jpg+productn.jpg': 'productlnl.jpg',
        };

        const selectedModuls = selectedOptions.filter(option => isModul(option));
        const key = selectedModuls.sort().join('+');

        return combinationMap[key] || (selectedModuls.length === 1 ? selectedModuls[0] : null);
    };

    const modulsCombiOrSingle = getModulsCombiOrSingle();

    return (
        <div className="yarbo-section">
            <div className="buttons-container">
                <div className="moduls">
                    <h2>Modulit</h2>
                    <button onClick={() => handleOptionClick('productl.jpg')} className={selectedOptions.includes('productl.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Lumilinko    {prices['productl.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Lumilingosta</a>
                    <button onClick={() => handleOptionClick('productn.jpg')} className={selectedOptions.includes('productn.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Nurmikonleikkuri    {prices['productn.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Nurmikonleikkurista</a>
                    <button onClick={() => handleOptionClick('productle.jpg')} className={selectedOptions.includes('productle.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Puhallin    {prices['productle.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Puhaltimesta</a>
                </div>
            </div>
            <div className="image-display">
                {modulsCombiOrSingle && (
                    <img src={`./images/make/${modulsCombiOrSingle}`} alt="Selected Option" />
                )}
            </div>
        </div>
    );
}

export default Make;
