import React, { useState } from 'react';
import './make.css';

function Make() {
    const [selectedOptions, setSelectedOptions] = useState([]);

    const isModul = (image) => ['productl.jpg', 'productn.jpg', 'productle.jpg'].includes(image);

    const handleOptionClick = (image) => {
        setSelectedOptions(prevSelectedOptions => {
            let updatedOptions;
            if (prevSelectedOptions.includes(image)) {
                updatedOptions = prevSelectedOptions.filter(option => option !== image);
            } else {
                updatedOptions = [...prevSelectedOptions, image];
            }
            return updatedOptions;
        });
    };

    const getModulsCombi = () => {
        const combinationMap = {
            'productl.jpg+productn.jpg': 'productln.jpg',
            'productl.jpg+productle.jpg': 'productlle.jpg',
            'productle.jpg+productn.jpg': 'productnle.jpg',
            'productl.jpg+productle.jpg+productn.jpg': 'productlnl.jpg',
        };

        const key = selectedOptions
            .filter(option => isModul(option))
            .sort()
            .join('+');

        return combinationMap[key] || null;
    };

    const modulsCombi = getModulsCombi();

    const visibleImages = (type) => selectedOptions
        .filter(option => type(option))
        .map((option, index) => (
            <img key={index} src={`./images/make/${option}`} alt={`Valittu ${index + 1}`} />
        ));

    return (
        <>
            <div className="yarbo-section">
                <div className="buttons-container full-width">
                    <div className="moduls">
                        <h2>Modulit</h2>
                        <button onClick={() => handleOptionClick('productl.jpg')} className={selectedOptions.includes('productl.jpg') ? 'active' : ''}>lumilinko</button>
                        <button onClick={() => handleOptionClick('productn.jpg')} className={selectedOptions.includes('productn.jpg') ? 'active' : ''}>nurmikonleikkuri</button>
                        <button onClick={() => handleOptionClick('productle.jpg')} className={selectedOptions.includes('productle.jpg') ? 'active' : ''}>Puhallin</button>
                        <div className="image-section">
                            {visibleImages(isModul)}
                        </div>
                    </div>
                </div>
                <div className="image-display full-width">
                    <h2>Yhdistelm√§ Modulit</h2>
                    {modulsCombi ? (
                        <img src={`./images/make/${modulsCombi}`} alt="Combined Option" />
                    ) : (
                        visibleImages(isModul)
                    )}
                </div>
            </div>
        </>
    );
}

export default Make;
