import React, { useState } from 'react';
import './make.css';

function lisavarusteet() {
    const [selectedAccessories, setSelectedAccessories] = useState({});
    const prices = {
        'Nestelevitin': 350,
        'Rakeinen levitin': 450
    };

    const handleAccessoryClick = (image) => {
        setSelectedAccessories((prevSelectedAccessories) => {
            const selected = prevSelectedAccessories[image];
            return { ...prevSelectedAccessories, [image]: !selected };
        });
    };

    const isActive = (image) => !!selectedAccessories[image];

    return (
        <div className="yarbo-section">
            <div className="buttons-container">
                <div className="lisavarusteet">
                    <h2>Lisävarusteet</h2>
                    <button onClick={() => handleAccessoryClick('gps.jpg')} className={isActive('gps.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>GPS {prices['gps.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa GPS:stä</a>
                    <button onClick={() => handleAccessoryClick('camera.jpg')} className={isActive('camera.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Kamera {prices['camera.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Kamerasta</a>
                    <button onClick={() => handleAccessoryClick('heater.jpg')} className={isActive('heater.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Lämmitin {prices['heater.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Lämmittimestä</a>
                </div>
            </div>
            <div className="image-display">
                {Object.keys(selectedAccessories).some(option => selectedAccessories[option]) && (
                    <img src={`./images/accessories/${Object.keys(selectedAccessories).filter(option => selectedAccessories[option]).join('')}`} alt="Selected Accessory" />
                )}
            </div>
        </div>
    );
}

export default lisavarusteet;
