import React, { useState } from 'react';
import './make.css';

function Make({ onPriceChange }) {
    const [selectedOptions, setSelectedOptions] = useState([]);
    const prices = {
        'productl.jpg': 2379, // Lumilingon moduuli
        'productn.jpg': 1629, // Ruohonleikkurin moduuli
        'productle.jpg': 1499, // Puhaltimen moduuli
        'productln.jpg': 9887, // Yarbo Lumilinko + Ruohonleikkuri
        'productlle.jpg': 9757, // Yarbo Lumilinko + Puhallin
        'productnle.jpg': 9007, // Yarbo Ruohonleikkuri + Puhallin
        'productlnl.jpg': 11386, // Yarbo 4-in-1 Piharobotti
    };

    const getCombinationPrice = (selectedMods) => {
        const combinationMap = {
            'productl.jpg+productn.jpg': 'productln.jpg',
            'productl.jpg+productle.jpg': 'productlle.jpg',
            'productle.jpg+productn.jpg': 'productnle.jpg',
            'productl.jpg+productle.jpg+productn.jpg': 'productlnl.jpg',
        };
        const key = selectedMods.sort().join('+');
        return combinationMap[key] || selectedMods.join('+');
    };

    const handleOptionClick = (image) => {
        setSelectedOptions(prevSelectedOptions => {
            let updatedOptions = prevSelectedOptions.includes(image)
                ? prevSelectedOptions.filter(option => option !== image)
                : [...prevSelectedOptions, image];

            const combiImage = getCombinationPrice(updatedOptions);
            const totalPrice = updatedOptions.reduce((total, item) => total + (prices[item] || 0), 0);

            const combinationPrice = prices[combiImage] || totalPrice;
            updatedOptions = updatedOptions.filter(opt => Object.keys(prices).includes(opt));

            onPriceChange(combinationPrice);
            return updatedOptions;
        });
    };

    const isActive = (image) => selectedOptions.includes(image);

    return (
        <div className="yarbo-section">
            <div className="buttons-container">
                <div className="moduls">
                    <h2>Modulit</h2>
                    <button onClick={() => handleOptionClick('productl.jpg')} className={isActive('productl.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Lumilinko {prices['productl.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Lumilingosta</a>
                    <button onClick={() => handleOptionClick('productn.jpg')} className={isActive('productn.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Nurmikonleikkuri {prices['productn.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Nurmikonleikkurista</a>
                    <button onClick={() => handleOptionClick('productle.jpg')} className={isActive('productle.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Puhallin {prices['productle.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Puhaltimesta</a>
                </div>
            </div>
            <div className="image-display">
                {getCombinationPrice(selectedOptions) && (
                    <img src={`./images/make/${getCombinationPrice(selectedOptions)}`} alt="Selected Combination" />
                )}
            </div>
        </div>
    );
}

export default Make;
