import React, { useState } from 'react';
import './make.css';
import Popup from './popup';

function Core() {
    const [selected, setSelected] = useState(false);
    const [popupImage, setPopupImage] = useState(null);
    const price = 5879; // Hinta 5 879€

    const handleButtonClick = () => {
        setSelected(!selected);
    };

    const openPopup = (image) => {
        setPopupImage(image);
    };

    const closePopup = () => {
        setPopupImage(null);
    };

    return (
        <div className="yarbo-section core">
            <div className="buttons-container">
                <div className="core-options">
                    <h2>Yarbocore</h2>
                    <button
                        onClick={handleButtonClick}
                        className={selected ? 'active' : ''}
                    >
                        <span className="checkmark"></span>Yarbocore {price.toLocaleString()}€
                    </button>
                    <a href="#" className="link-button" onClick={() => openPopup('./images/make/core-popup.png')}>Lisätietoa</a>
                </div>
            </div>
            <div className="image-display">
                {selected && (
                    <img src="./images/make/core.png" alt="Yarbocore Option" />
                )}
            </div>
            {popupImage && <Popup image={popupImage} onClose={closePopup} />}
        </div>
    );
}

export default Core;
