import React, { useEffect } from 'react';
import './menu2.css';
import { Link } from "react-router-dom";

const Menu2 = () => {
    useEffect(() => {
        const handleScroll = () => {
            const menu2 = document.querySelector('.menu2');
            const placeholder = document.querySelector('.menu2.placeholder');

            if (window.scrollY > 50) {
                menu2.classList.add('fixed');
                placeholder.style.display = 'block';
            } else {
                menu2.classList.remove('fixed');
                placeholder.style.display = 'none';
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <>
            <nav className="menu2">
                <ul>
                    <li><a href="#Make">Moduulit</a></li>
                    <li><a href="#Liitteet">Liitännät</a></li>
                    <li><a href="#Lisavarusteet"></a></li>
                    <li><a href="#Prices">Hinnat</a></li>
                    <li><Link to="/Contact">Yhteydenotto</Link></li>
                    <li><Link to="/">Etusivu</Link></li>
                </ul>
            </nav>
            <div className="menu2 placeholder"></div>
        </>
    );
};

export default Menu2;
