import React, { useState } from 'react';
import './make.css';

function Lisaosat({ onPriceChange }) {
    const [selectedOptions, setSelectedOptions] = useState([]);
    const prices = {
        'blade.jpg': 500, // Esimerkkihinta
        'battery.jpg': 400, // Esimerkkihinta
        'koukku.jpg': 300 // Esimerkkihinta
    };

    const isLisaosa = (image) => ['blade.jpg', 'battery.jpg', 'koukku.jpg'].includes(image);

    const handleOptionClick = (image) => {
        setSelectedOptions(prevSelectedOptions => {
            let updatedOptions;
            if (prevSelectedOptions.includes(image)) {
                updatedOptions = prevSelectedOptions.filter(option => option !== image);
                onPriceChange(-prices[image]);
            } else {
                updatedOptions = [...prevSelectedOptions, image];
                onPriceChange(prices[image]);
            }
            return updatedOptions;
        });
    };

    const getLisaosaCombiOrSingle = () => {
        const combinationMap = {
            'blade.jpg+koukku.jpg': 'koukkublade.jpg',
            'battery.jpg+blade.jpg': 'batteryblade.jpg',
            'battery.jpg+koukku.jpg': 'batterykoukku.jpg',
            'battery.jpg+blade.jpg+koukku.jpg': 'lisat.jpg',
        };

        const selectedLisaosat = selectedOptions.filter(option => isLisaosa(option));
        const key = selectedLisaosat.sort().join('+');

        return combinationMap[key] || (selectedLisaosat.length === 1 ? selectedLisaosat[0] : null);
    };

    const lisaosaCombiOrSingle = getLisaosaCombiOrSingle();

    return (
        <div className="yarbo-section">
            <div className="buttons-container">
                <div className="lisaosat">
                    <h2>Lisäosat</h2>
                    <button onClick={() => handleOptionClick('blade.jpg')} className={selectedOptions.includes('blade.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Aura    {prices['blade.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Aurasta</a>
                    <button onClick={() => handleOptionClick('battery.jpg')} className={selectedOptions.includes('battery.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Akku    {prices['battery.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Akusta</a>
                    <button onClick={() => handleOptionClick('koukku.jpg')} className={selectedOptions.includes('koukku.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Vetokoukku    {prices['koukku.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Vetokoukusta</a>
                </div>
            </div>
            <div className="image-display">
                {lisaosaCombiOrSingle && (
                    <img src={`./images/make/${lisaosaCombiOrSingle}`} alt="Selected Option" />
                )}
            </div>
        </div>
    );
}

export default Lisaosat;
