import React, { useState } from 'react';
import './make.css';

function Make() {
    const [selectedOptions, setSelectedOptions] = useState([]); // Use state to keep track of selected options

    const handleOptionClick = (image) => {
        setSelectedOptions(prevSelectedOptions => {
            if (prevSelectedOptions.includes(image)) {
                return prevSelectedOptions.filter(option => option !== image);
            } else {
                return [...prevSelectedOptions, image];
            }
        });
    };

    const getCombinedImage = () => {
        const combinationMap = {
            'productl.jpg+productn.jpg': 'productln.jpg',
            'productl.jpg+productle.jpg': 'productlle.jpg',
            'productle.jpg+productn.jpg': 'productnle.jpg',
            'productl.jpg+productn.jpg+productle.jpg': 'productlnl.jpg',
        };

        const key = selectedOptions.sort().join('+');
        return combinationMap[key] || selectedOptions;
    };

    const combinedImage = getCombinedImage();

    return (
        <div className="yarbo-section">
            <div className="image-display">
                {Array.isArray(combinedImage) ? combinedImage.map((option, index) => (
                    <img key={index} src={`./images/make/${option}`} alt={`Selected Option ${index + 1}`} />
                )) : (
                    <img src={`./images/make/${combinedImage}`} alt="Combined Option" />
                )}
            </div>
            <div className="buttons-container">
                <div className="moduls">
                    <h2>Modulit</h2>
                    <button onClick={() => handleOptionClick('productl.jpg')} className={selectedOptions.includes('productl.jpg') ? 'active' : ''}>lumilinko</button>
                    <button onClick={() => handleOptionClick('productn.jpg')} className={selectedOptions.includes('productn.jpg') ? 'active' : ''}>nurmikonleikkuri</button>
                    <button onClick={() => handleOptionClick('productle.jpg')} className={selectedOptions.includes('productle.jpg') ? 'active' : ''}>Puhallin</button>
                </div>
                <div className="lisaosat">
                    <h2>Lis√§osat</h2>
                    <button onClick={() => handleOptionClick('product4.jpg')} className={selectedOptions.includes('product4.jpg') ? 'active' : ''}>Option 4</button>
                    <button onClick={() => handleOptionClick('product5.jpg')} className={selectedOptions.includes('product5.jpg') ? 'active' : ''}>Option 5</button>
                    <button onClick={() => handleOptionClick('product6.jpg')} className={selectedOptions.includes('product6.jpg') ? 'active' : ''}>Option 6</button>
                </div>
            </div>
        </div>
    );
}

export default Make;
