import React, { useState } from 'react';
import './make.css';

function Make() {
    const [selectedOption, setSelectedOption] = useState(null); // Päivitetty määrittämään yhden valitun vaihtoehdon

    const handleOptionClick = (image) => {
        setSelectedOption(image); // Päivitetty vaihtamaan valitun vaihtoehdon
    };

    return (
        <div className="yarbo-section">
            <div className="options">
                <button onClick={() => handleOptionClick('puplic/imagines/make/productl')} className={selectedOption === 'image1.jpg' ? 'active' : ''}>lumilinko</button>
                <button onClick={() => handleOptionClick('puplic/imagines/make/productn')} className={selectedOption === 'image2.jpg' ? 'active' : ''}>nurmikonleikkuri</button>
                <button onClick={() => handleOptionClick('image3.jpg')} className={selectedOption === 'image3.jpg' ? 'active' : ''}>Option 3</button>
                <button onClick={() => handleOptionClick('image4.jpg')} className={selectedOption === 'image4.jpg' ? 'active' : ''}>Option 4</button>
                <button onClick={() => handleOptionClick('image5.jpg')} className={selectedOption === 'image5.jpg' ? 'active' : ''}>Option 5</button>
            </div>
            <div className="image-display">
                {selectedOption && <img src={`path_to_images/${selectedOption}`} id="displayed-image" alt="Displayed" />} {/* Päivitetty kuvaosio vastaamaan valittua kuvaa */}
            </div>
        </div>
    );
}

export default Make;
