import React, { useRef, useState } from 'react';
import emailjs from 'emailjs-com';
import '../componentscontact/EmailSender.css';

const EmailSender2 = () => {
    const form = useRef();
    const [selectedButtons, setSelectedButtons] = useState([]);

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.sendForm('service_28112024', 'template_tpru27a', form.current, 'L4M6jh_xRukKF_ap3')
            .then((result) => {
                console.log(result.text);
                alert('Viesti lähetetty onnistuneesti!');
            }, (error) => {
                console.log(error.text);
                alert('Viestiä ei voitu lähettää.');
            });
    };

    const addName = (name) => {
        setSelectedButtons([...selectedButtons, name]);
    };

    return (
        <>
            <div>
                <h3>Valitut osat:</h3>
                <ul>
                    {selectedButtons.map((name, index) => (
                        <li key={index}>{name}</li>
                    ))}
                </ul>
            </div>

            <button onClick={() => addName('Nimi 1')}>Nimi 1</button>
            <button onClick={() => addName('Nimi 2')}>Nimi 2</button>
            <button onClick={() => addName('Nimi 3')}>Nimi 3</button>

            <form ref={form} onSubmit={sendEmail}>
                <label>Nimi</label>
                <input type="text" name="name" required />
                <label>Sähköposti</label>
                <input type="email" name="email" required />
                <label>Viesti</label>
                <textarea name="message" required />
                <input type="submit" value="Lähetä" />
            </form>
        </>
    );
};

export default EmailSender2;
