import React, { useState } from 'react';
import './make.css';
import Popup from './popup';

function Lisavarusteet() {
    const [selectedAccessories, setSelectedAccessories] = useState({});
    const [popupImage, setPopupImage] = useState(null);
    const prices = {
        'Nestelevitin.png': 1159,
        'Rakeidenlevitin.png': 1159
    };

    const handleAccessoryClick = (image) => {
        setSelectedAccessories((prevSelectedAccessories) => {
            const selected = prevSelectedAccessories[image];
            return { ...prevSelectedAccessories, [image]: !selected };
        });
    };

    const isActive = (image) => !!selectedAccessories[image];

    const getCombinationImage = () => {
        const activeAccessories = Object.keys(selectedAccessories).filter(option => selectedAccessories[option]);

        if (activeAccessories.length === 2) {
            return 'lisavarusteet.png';
        }

        return activeAccessories[0];
    };

    const openPopup = (image) => {
        setPopupImage(image);
    };

    const closePopup = () => {
        setPopupImage(null);
    };

    return (
        <div className="yarbo-section">
            <div className="buttons-container">
                <div className="lisavarusteet">
                    <h2>Lisävarusteet</h2>
                    <button onClick={() => handleAccessoryClick('Nestelevitin.png')} className={isActive('Nestelevitin.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Nestelevitin {prices['Nestelevitin.png']} €
                    </button>
                    <a href="#" className="link-button" onClick={() => openPopup('./images/lisavarusteet/nestelevitin-popup.png')}>Lisätietoa Nestelevittimestä</a>
                    <button onClick={() => handleAccessoryClick('Rakeidenlevitin.png')} className={isActive('Rakeidenlevitin.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Rakeidenlevitin {prices['Rakeidenlevitin.png']} €
                    </button>
                    <a href="#" className="link-button" onClick={() => openPopup('./images/lisavarusteet/rakeidenlevitin-popup.png')}>Lisätietoa Rakeidenlevittimestä</a>
                </div>
            </div>
            <div className="image-display">
                {Object.keys(selectedAccessories).some(option => selectedAccessories[option]) && (
                    <img src={`./images/make/${getCombinationImage()}`} alt="Selected Accessory Combination" />
                )}
            </div>
            {popupImage && <Popup image={popupImage} onClose={closePopup} />}
        </div>
    );
}

export default Lisavarusteet;
