import React, { useState } from 'react';
import './make.css';

function Make() {
    const [selectedOptions, setSelectedOptions] = useState([]); // Use state to keep track of selected options
    const [hiddenImages, setHiddenImages] = useState([]); // To track hidden images

    const isModul = (image) => ['productl.jpg', 'productn.jpg', 'productle.jpg'].includes(image);
    const isLisaosa = (image) => ['blade.jpg', 'battery.jpg', 'koukku.jpg'].includes(image);

    const handleOptionClick = (image) => {
        setSelectedOptions(prevSelectedOptions => {
            let updatedOptions;
            if (prevSelectedOptions.includes(image)) {
                updatedOptions = prevSelectedOptions.filter(option => option !== image);
            } else {
                updatedOptions = [...prevSelectedOptions, image];
            }

            if (isModul(image)) {
                setHiddenImages(updatedOptions.filter(isLisaosa));
            } else {
                setHiddenImages(updatedOptions.filter(isModul));
            }

            return updatedOptions;
        });
    };

    const getCombinedImage = () => { //Yhdistelmä kuvat
        const combinationMap = {
            'productl.jpg+productn.jpg': 'productln.jpg',
            'productl.jpg+productle.jpg': 'productlle.jpg',
            'productle.jpg+productn.jpg': 'productnle.jpg',
            'productl.jpg+productle.jpg+productn.jpg': 'productlnl.jpg',

            'productl.jpg+blade.jpg+battery.jpg': 'productlbnk.jpg',
            'blade.jpg+battery.jpg+koukku.jpg': 'productnle.jpg',
        };

        const key = selectedOptions
            .filter(option => !hiddenImages.includes(option))
            .sort()
            .join('+');

        return combinationMap[key] || selectedOptions;
    };

    const combinedImage = getCombinedImage();

    return (
        <div className="yarbo-section">
            <div className="image-display">
                {Array.isArray(combinedImage) ? combinedImage.map((option, index) => (
                    <img key={index} src={`./images/make/${option}`} alt={`Selected Option ${index + 1}`} />
                )) : (
                    <img src={`./images/make/${combinedImage}`} alt="Combined Option" />
                )}
            </div>
            <div className="buttons-container">
                <div className="moduls">
                    <h2>Modulit</h2>
                    <button onClick={() => handleOptionClick('productl.jpg')} className={selectedOptions.includes('productl.jpg') ? 'active' : ''}>lumilinko</button>
                    <button onClick={() => handleOptionClick('productn.jpg')} className={selectedOptions.includes('productn.jpg') ? 'active' : ''}>nurmikonleikkuri</button>
                    <button onClick={() => handleOptionClick('productle.jpg')} className={selectedOptions.includes('productle.jpg') ? 'active' : ''}>Puhallin</button>
                </div>
                <div className="lisaosat">
                    <h2>Lisäosat</h2>
                    <button onClick={() => handleOptionClick('blade.jpg')} className={selectedOptions.includes('blade.jpg') ? 'active' : ''}>Aura</button>
                    <button onClick={() => handleOptionClick('battery.jpg')} className={selectedOptions.includes('battery.jpg') ? 'active' : ''}>Akku</button>
                    <button onClick={() => handleOptionClick('koukku.jpg')} className={selectedOptions.includes('koukku.jpg') ? 'active' : ''}>Vetokoukku</button>
                </div>
            </div>
        </div>
    );
}

export default Make;
