import React, { useState } from 'react';
import './make.css';

function Make() {
    const [selectedOptions, setSelectedOptions] = useState([]);

    const handleOptionClick = (image) => {
        setSelectedOptions(prevSelectedOptions => {
            if (prevSelectedOptions.includes(image)) {
                return prevSelectedOptions.filter(option => option !== image);
            } else {
                return [...prevSelectedOptions, image];
            }
        });
    };

    const getCombinationImage = () => {
        if (selectedOptions.length === 0) {
            return 'default.jpg';
        }
        return `combined_images/${selectedOptions.join('+')}.jpg`;
    };

    return (
        <div className="yarbo-section">
            <div className="options">
                <button onClick={() => handleOptionClick('image1.jpg')} className={selectedOptions.includes('productn') ? 'active' : ''}>lumilinko</button>
                <button onClick={() => handleOptionClick('image2.jpg')} className={selectedOptions.includes('image2.jpg') ? 'active' : ''}>nurmikonleikkuri</button>
                <button onClick={() => handleOptionClick('image3.jpg')} className={selectedOptions.includes('image3.jpg') ? 'active' : ''}>Option 3</button>
                <button onClick={() => handleOptionClick('image4.jpg')} className={selectedOptions.includes('image4.jpg') ? 'active' : ''}>Option 4</button>
                <button onClick={() => handleOptionClick('image5.jpg')} className={selectedOptions.includes('image5.jpg') ? 'active' : ''}>Option 5</button>
            </div>
            <div className="image-display">
                <img src={getCombinationImage()} id="displayed-image" alt="Displayed" />
            </div>
        </div>
    );
}

export default Make;
