import React, { useState } from 'react';
import './make.css';

function Lisaosat() {
    const [selectedOptions, setSelectedOptions] = useState([]);

    const isLisaosa = (image) => ['blade.jpg', 'battery.jpg', 'koukku.jpg'].includes(image);

    const handleOptionClick = (image) => {
        setSelectedOptions(prevSelectedOptions => {
            let updatedOptions;
            if (prevSelectedOptions.includes(image)) {
                updatedOptions = prevSelectedOptions.filter(option => option !== image);
            } else {
                updatedOptions = [...prevSelectedOptions, image];
            }
            return updatedOptions;
        });
    };

    const getLisaosaCombiOrSingle = () => {
        const combinationMap = {
            'blade.jpg+koukku.jpg': 'koukkublade.jpg',
            'battery.jpg+blade.jpg': 'batteryblade.jpg',
            'battery.jpg+koukku.jpg': 'batterykoukku.jpg',
            'battery.jpg+blade.jpg+koukku.jpg': 'lisat.jpg',
        };

        const individualImageMap = {
            'blade.jpg': 'blade.jpg',
            'battery.jpg': 'battery.jpg',
            'koukku.jpg': 'koukku.jpg',
        };

        const selectedLisaosat = selectedOptions.filter(option => isLisaosa(option));
        const key = selectedLisaosat.sort().join('+');

        return combinationMap[key] || (selectedLisaosat.length === 1 ? individualImageMap[selectedLisaosat[0]] : null);
    };

    const lisaosaCombiOrSingle = getLisaosaCombiOrSingle();

    return (
        <>
            <div className="yarbo-section">
                <div className="buttons-container">
                    <div className="lisaosat">
                        <h2>Lis√§osat</h2>
                        <button onClick={() => handleOptionClick('blade.jpg')} className={selectedOptions.includes('blade.jpg') ? 'active' : ''}>Aura</button>
                        <button onClick={() => handleOptionClick('battery.jpg')} className={selectedOptions.includes('battery.jpg') ? 'active' : ''}>Akku</button>
                        <button onClick={() => handleOptionClick('koukku.jpg')} className={selectedOptions.includes('koukku.jpg') ? 'active' : ''}>Vetokoukku</button>
                    </div>
                </div>
                <div className="image-display">
                    {lisaosaCombiOrSingle && (
                        <img src={`./images/make/${lisaosaCombiOrSingle}`} alt="Selected Option" />
                    )}
                </div>
            </div>
        </>
    );
}

export default Lisaosat;
