import React, { useState } from 'react';
import './make.css';
import Popup from './popup';

function Make() {
    const [selectedOptions, setSelectedOptions] = useState([]);
    const [popupImage, setPopupImage] = useState(null);

    const prices = {
        'productl.jpg': 2379,
        'productn.jpg': 1629,
        'productle.jpg': 1499,
    };

    const handleOptionClick = (image) => {
        setSelectedOptions((prevSelectedOptions) => {
            if (prevSelectedOptions.includes(image)) {
                return prevSelectedOptions.filter(option => option !== image);
            } else {
                return [...prevSelectedOptions, image];
            }
        });
    };

    const isActive = (image) => selectedOptions.includes(image);

    return (
        <div className="yarbo-section">
            <div className="buttons-container">
                <div className="moduls">
                    <h2>Modulit</h2>
                    <button onClick={() => handleOptionClick('productl.jpg')} className={isActive('productl.jpg') ? 'active' : ''}>
                        <span className="checkmark">{isActive('productl.jpg') ? '✓' : '○'}</span>Lumilinko {prices['productl.jpg']} €
                    </button>
                    <a href="#" className="link-button" onClick={() => openPopup('./images/make/li.jpg')}>Lisätietoa</a>
                    <button onClick={() => handleOptionClick('productn.jpg')} className={isActive('productn.jpg') ? 'active' : ''}>
                        <span className="checkmark">{isActive('productn.jpg') ? '✓' : '○'}</span>Nurmikonleikkuri {prices['productn.jpg']} €
                    </button>
                    <a href="#" className="link-button" onClick={() => openPopup('./images/make/ni.jpg')}>Lisätietoa</a>
                    <button onClick={() => handleOptionClick('productle.jpg')} className={isActive('productle.jpg') ? 'active' : ''}>
                        <span className="checkmark">{isActive('productle.jpg') ? '✓' : '○'}</span>Puhallin {prices['productle.jpg']} €
                    </button>
                    <a href="#" className="link-button" onClick={() => openPopup('./images/make/lei.jpg')}>Lisätietoa</a>
                </div>
            </div>
            <div className="image-display">
                {selectedOptions.length > 0 && (
                    <img src={`./images/make/${getCombinationImage()}`} alt="Selected Option" />
                )}
            </div>
            {popupImage && <Popup image={popupImage} onClose={closePopup} />}
        </div>
    );
}

export default Make;
