import React, { useState } from 'react';
import './make.css';

function Lisaosat({ onPriceChange }) {
    const [selectedOptions, setSelectedOptions] = useState([]);
    const prices = {
        'blade.jpg': 500, // Esimerkkihinta
        'battery.jpg': 400, // Esimerkkihinta
        'koukku.jpg': 300 // Esimerkkihinta
    };

    const handleOptionClick = (image) => {
        setSelectedOptions((prevSelectedOptions) => {
            if (prevSelectedOptions.includes(image)) {
                // Kun valinta poistetaan, vähennä hinnasta kerran
                onPriceChange(-prices[image]);
                return prevSelectedOptions.filter(option => option !== image);
            } else {
                // Kun lisäosa valitaan, lisää hintaan kerran
                onPriceChange(prices[image]);
                return [...prevSelectedOptions, image];
            }
        });
    };

    const isActive = (image) => selectedOptions.includes(image);

    return (
        <div className="yarbo-section">
            <div className="buttons-container">
                <div className="lisaosat">
                    <h2>Lisäosat</h2>
                    <button onClick={() => handleOptionClick('blade.jpg')} className={isActive('blade.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Aura {prices['blade.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Aurasta</a>
                    <button onClick={() => handleOptionClick('battery.jpg')} className={isActive('battery.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Akku {prices['battery.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Akusta</a>
                    <button onClick={() => handleOptionClick('koukku.jpg')} className={isActive('koukku.jpg') ? 'active' : ''}>
                        <span className="checkmark"></span>Vetokoukku {prices['koukku.jpg']} €
                    </button>
                    <a href="#" className="link-button">Lisätietoa Vetokoukusta</a>
                </div>
            </div>
            <div className="image-display">
                {selectedOptions.length > 0 && (
                    <img src={`./images/make/${selectedOptions.join('')}`} alt="Selected Option" />
                )}
            </div>
        </div>
    );
}

export default Lisaosat;
